const initialState = {
  list: [],
};
const List = (state = initialState.list, action) => {
  switch (action.type) {
    case "ADD_LIST": {
      const product = action
      return [...state, action.payload];
    }
    case "INCREMENT_AMOUNT": {
      const id = action.payload;
      state.forEach((e) => {
        if (e.id === id) {
          e.amount++;
        }
      });
      return [...state];
    }
    case "DECREMENT_AMOUNT": {
      const id = action.payload;
      state.forEach((e) => {
        if (e.id === id) {
          if (e.amount > 1) {
            e.amount--;
          }
        }
      });
      return [...state];
    }
    default:
      return state;
  }
};

export default List;
