const initialState = { list: [], amount: 1 };

const addList = (state = initialState.list, action) => {
  switch (action.type) {
    case "ADD_LIST": {
      return {...state, list: [...action.payload]};
    }
    case "INCREMENT_AMOUNT": {
      return [];
    }
    case "DECREMENT_AMOUNT": {
      return [...state, state.payload - 1];
    }
    default:
      return state;
  }
};

export default addList;
