import React, { useEffect, useState } from "react";
import { useDispatch, useSelector } from "react-redux";
import { addList } from "../../action/List";
import "antd/dist/antd.css";
import { List, Card, message, Button } from "antd";
import { ShoppingCartOutlined } from "@ant-design/icons";
import CarouselHome from "../../components/carousel/Carousel";
import callApi from "../../api/ApiCaller";

export default function Home() {
  const [data, setData] = useState();

  const dispatch = useDispatch();
  const listCart = useSelector((state) => state.list);

  useEffect(() => {
    async function fetchData() {
      await callApi("get", "products", null)
        .then((res) => {
          setData(res.data);
        })
        .catch((err) => console.log(err));
    }
    fetchData();
  }, []);

  const { Meta } = Card;

  //Send a product to CART
  const sendProduct = (id) => {
    listCart.forEach((e) => {
      if (e.id === id) {
        id = null;
        message.info(`${e.name} is already in cart`);
      }
    });
    data.forEach((item) => {
      if (item.id === id) {
        dispatch(addList(item));
        message.success(`${item.name} add to Cart successfully!`);
      }
    });
  };

  return (
    <>
      <CarouselHome />
      <List
        grid={{
          gutter: 16,
          xs: 1,
          sm: 2,
          md: 3,
          lg: 4,
          xl: 5,
          xxl: 6,
        }}
        pagination="true"
        dataSource={data}
        renderItem={(item) => (
          <List.Item>
            <Card title={item.name}>
            {/* <img style={{backgroundSize: 'cover'}} alt="Products list" src={item.image} /> */}
              {item.description}
              <Button></Button>

              </Card>
            {/* <Card
              title={item.name}
              hoverable="true"
              style={{ width: 190 }}
              cover={<img alt="Products list" src={item.image} />}
              actions={[
                <Button type="link" onClick={() => sendProduct(item.id)}>
                  Cart <ShoppingCartOutlined />
                </Button>,
              ]}
            >
              <Meta description={item.description + " " + item.price + " $"} />
            </Card> */}
          </List.Item>
        )}
      />
    </>
  );
}
