import React from "react";
import { Badge, Avatar, Popover } from "antd";
import { BellOutlined } from "@ant-design/icons";

export default function Notification() {
  const text = <span>Title</span>;
  const content = (
    <div>
      <p>Content</p>
      <p>Content</p>
    </div>
  );
  return (
    <div>
      <Popover
        placement="bottom"
        title={text}
        content={content}
        trigger="click"
      >
        <Badge count={1}>
          <Avatar
            // onClick={() => onClickAvatar()}
            style={{ backgroundColor: "grey", cursor: "pointer" }}
            icon={<BellOutlined />}
          />
        </Badge>
      </Popover>
    </div>
  );
}
