import React from "react";
import { Badge, Avatar, Popover, List, Button } from "antd";
import { BellOutlined, SettingOutlined } from "@ant-design/icons";

export default function Notification() {
  const data = [
    {
      title: "Racing car sprays burning fuel into crowd",
    },
    {
      title: "Racing car sprays burning fuel into crowd",
    },
    {
      title: "Racing car sprays burning fuel into crowd",
    },
    {
      title:
        "Racing car sprays burning fuel into crowd,Racing car sprays burning fuel into crowd",
    },
  ];
  const text = (
    <div style={{ display: "flex" }}>
      <p style={{ margin: "0px auto" }} href>
        Notifications
      </p>
    </div>
  );
  const content = (
    <div style={{ width: 300 }}>
      <List
        footer={
          <div style={{ display: "flex" }}>
            <a  style={{ margin: "0px auto" }} href>
              Show all notifications
            </a>
          </div>
        }
        size="small"
        dataSource={data}
        renderItem={(item) => (
          <List.Item>
          <div>
            <div style={{ display: "flex", alignItems: "center" }}>
              <div style={{ marginRight: 7 }}>
                <Avatar style={{ backgroundColor: "#3B5998" }}>
                  <SettingOutlined />
                </Avatar>
              </div>
              <a href="localhost:3000">{item.title}</a>
            </div>
          </div>
           </List.Item>
        )}
      />
    </div>
  );
  return (
    <div>
      <Popover
        placement="bottom"
        title={text}
        content={content}
        trigger="click"
      >
        <Badge count={data.length}>
          <Avatar
            style={{ backgroundColor: "#4267b2", cursor: "pointer" }}
            icon={<BellOutlined />}
          />
        </Badge>
      </Popover>
    </div>
  );
}
