import callApi from '../api/ApiCaller';

async function fetchData() {
  const res = await callApi("get", "products", null)
  return res.data
}
const data = fetchData()


const dataProducts = (state = [data], action) => {
  switch (action.type) {

    default:
      return state;
  }
};
export default dataProducts;
