const addList = (state = [], action) => {
  switch (action.type) {
    case "ADD_LIST": {
      return [...state, action.payload];
    }
    case "INCREMENT_AMOUNT": {
      return state.map((item,index)=>{
        if (item.index === action.payload) {
          return item.amount + 1         
        }
      });
    }
    default:
      return state;
  }
};


export default addList;

