import React,{useEffect,useState} from "react";
import "antd/dist/antd.css";

import {
  Table,
  Tag,
  Button,
  Form,
  Input,
  Select,
  DatePicker,
  InputNumber,
} from "antd";
import callApi from "../api/ApiCaller";

const columns = [
  {
    title: "Product",
    dataIndex: "product",
    key: "product",
    render: (text) => text,
  },
  {
    title: "Cost",
    dataIndex: "cost",
    key: "cost",
  },
  {
    title: "Description",
    dataIndex: "description",
    key: "description",
  },
  {
    title: "JoinDay",
    dataIndex: "joinday",
    key: "joinday",
  },
  {
    title: "Stock",
    key: "stock",
    dataIndex: "stock",
    render: (stock) => (
      <span>
        {stock.map((tag) => {
          let color = tag.length > 8 ? "volcano" : "green";
          return (
            <Tag color={color} key={tag}>
              {tag.toUpperCase()}
            </Tag>
          );
        })}
      </span>
    ),
  },
  {
    title: "Action",
    key: "action",
    render: (text, record) => (
      <span>
        <Button type="primary">Edit</Button>
        <Button type="danger">Delete</Button>
      </span>
    ),
  },
];



