//     description: "Year in 2018",
//     joinday: '01/04/2019',
//     stock: ["In Stock"],
//   },
// ];

export default function MancostrUsers() {
  const [data, setData] = useState()
  useEffect(() => {
    callApi('get', 'products',null).then(res=>setData(res))
    .then(console.log(data))

  }, [])


  return (
    <div>
      {/* <Table columns={columns} dataSource={data} /> */}
      <Form labelCol={{ span: 4 }} wrapperCol={{ span: 40 }} layout="inline">
        <Form.Item>
          <Input placeholder="Product" />
        </Form.Item>
        <Form.Item>
          <InputNumber placeholder="Cost" />
        </Form.Item>
        <Form.Item>
          <Input placeholder="description" />
        </Form.Item>
        <Form.Item>
          <Select placeholder="Stock">
            <Select.Option value="In Stock">In Stock</Select.Option>
            <Select.Option value="Out Stock">Out Stock</Select.Option>
          </Select>
        </Form.Item>
        <Form.Item>
          <DatePicker placeholder="JoinDay" />
        </Form.Item>
        <Form.Item>
          <Button type="primary">Add</Button>
        </Form.Item>
      </Form>
    </div>
  );
}
