const initialState = []
const addlist = (state = initialState, action) => {
  switch (action.type) {
    case "ADD_LIST": {
      const newList = [...state];
      newList.push(action.payload);
      return {
        list: newList,
      };
    }
    default:
      return state;
  }
};
export default list;
