import React from "react";
import "antd/dist/antd.css";
import {
  Table,
  Tag,
  Button,
  Form,
  Input,
  Select,
  DatePicker,
  InputNumber,
} from "antd";

const columns = [
  {
    title: "Product",
    dataIndex: "product",
    key: "product",
    render: (text) => text,
  },
  {
    title: "Cost",
    dataIndex: "cost",
    key: "cost",
  },
  {
    title: "Description",
    dataIndex: "description",
    key: "description",
  },
  {
    title: "Stock",
    key: "stock",
    dataIndex: "stock",
    render: (stock) => (
      <span>
        {stock.map((tag) => {
          let color = tag.length > 8 ? "volcano" : "green";
          return (
            <Tag color={color} key={tag}>
              {tag.toUpperCase()}
            </Tag>
          );
        })}
      </span>
    ),
  },
  {
    title: "Action",
    key: "action",
    render: (text, record) => (
      <span>
        <Button primary>Edit</Button>
        <Button danger>Delete</Button>
      </span>
    ),
  },
];

const data = [
  {
    key: "1",
    product: "Iphone SE",
    cost: 32,
    description: "Bonic A13, super Reitina",
    stock: ["In Stock"],
  },
  {
    key: "2",
    product: "Iphone 11",
    cost: 42,
    description: "London No. 1 Lake Park",
    stock: ["Out Stock"],
  },
  {
    key: "3",
    product: "Iphone xs Mar",
    cost: 32,
    description: "Year in 2019",
    stock: ["In Stock"],
  },
];

export default function MancostrUsers() {
  return (
    <div>
      <Table rowKey="uid" columns={columns} dataSource={data} />
      <Form labelCol={{ span: 4 }} wrapperCol={{ span: 40 }} layout="inline">
        <Form.Item>
          <Input placeholder="Product" />
        </Form.Item>
        <Form.Item>
          <InputNumber placeholder="Cost" />
        </Form.Item>
        <Form.Item>
          <Input placeholder="description" />
        </Form.Item>
        <Form.Item>
          <Select placeholder="Stock">
            <Select.Option value="In Stock">In Stock</Select.Option>
            <Select.Option value="Out Stock">Out Stock</Select.Option>
          </Select>
        </Form.Item>

        <Form.Item>
          <DatePicker placeholder="JoinDay" />
        </Form.Item>

        <Form.Item>
          <Button type="primary">Add</Button>
        </Form.Item>
      </Form>
    </div>
  );
}
