import React, { useEffect, useState } from "react";
import { Carousel } from "antd";
import "./Carousel.css";
import callApi from "../"

export default function CarouselHome() {
  const [data, setData] = useState()
  useEffect(() => {
    async function fetchData() {
      await callApi("get", "Carousel", null)
        .then((res) => {
          setData(res.data);
        })
        .catch((err) => console.log(err));
    }
    fetchData();
  }, []);
  const loadCarousel = () => {
    data.map(item=><div><img alt="Carousel" src={item.image}/></div>)
  }
  return (
    <Carousel effect="fade" autoplay>
      {loadCarousel()}
      <div>
        <h3>1</h3>
      </div>
      <div>
        <h3>2</h3>
      </div>
      <div>
        <h3>3</h3>
      </div>
      <div>
        <h3>4</h3>
      </div>
    </Carousel>
  );
}
