import React from "react";
import "antd/dist/antd.css";
import {
  Table,
  Tag,
  Button,
  Form,
  Input,
  Radio,
  Select,
  Cascader,
  DatePicker,
  InputNumber,
  TreeSelect,
  Switch,
} from "antd";

const columns = [
  {
    title: "Name",
    dataIndex: "name",
    key: "name",
    render: (text) => text,
  },
  {
    title: "Age",
    dataIndex: "age",
    key: "age",
  },
  {
    title: "Address",
    dataIndex: "address",
    key: "address",
  },
  {
    title: "Tags",
    key: "tags",
    dataIndex: "tags",
    render: (tags) => (
      <span>
        {tags.map((tag) => {
          let color = tag.length > 5 ? "green" : "volcano";
          return (
            <Tag color={color} key={tag}>
              {tag.toUpperCase()}
            </Tag>
          );
        })}
      </span>
    ),
  },
  {
    title: "Action",
    key: "action",
    render: (text, record) => (
      <span>
        <Button primary>Edit</Button>
        <Button danger>Delete</Button>
      </span>
    ),
  },
];

const data = [
  {
    key: "1",
    name: "John Brown",
    age: 32,
    address: "New York No. 1 Lake Park",
    tags: ["Còn hàng"],
  },
  {
    key: "2",
    name: "Jim Green",
    age: 42,
    address: "London No. 1 Lake Park",
    tags: ["Hết"],
  },
  {
    key: "3",
    name: "Joe Black",
    age: 32,
    address: "Sidney No. 1 Lake Park",
    tags: ["Còn hàng"],
  },
];

export default function ManagerUsers() {
  return (
    <div>
      <Table columns={columns} dataSource={data} />
      <Form
        labelCol={{ span: 4 }}
        wrapperCol={{ span: 40 }}
        layout="inline"
      >
        <Form.Item>
          <Input placeholder="Name"/>
        </Form.Item>
        <Form.Item>
          <Select placeholder="Permission">
            <Select.Option value="demo">Admin</Select.Option>
            <Select.Option value="demo">Manager</Select.Option>
            <Select.Option value="demo">Normal</Select.Option>
          </Select>
        </Form.Item>

        <Form.Item >
          <DatePicker placeholder="JoinDay"/>
        </Form.Item>
        <Form.Item>
          <InputNumber placeholder="Age" />
        </Form.Item>

        <Form.Item>
          <Button type="">Add</Button>
        </Form.Item>
      </Form>
    </div>
  );
}
