// const initialState = []
const addList = (state = [], action) => {
  switch (action.type) {
    case "ADD_LIST": {
      return [...state, action.payload];
    }
    case "INCREMENT_AMOUNT": {
      return [...state, state.amount + 1];
    }
    case "DECREMENT_AMOUNT": {
      return [...state, state.amount - 1];
    }
    default:
      return state;
  }
};
export default addList;
