import React, { useState } from "react";
import { Modal, Button, Form, Input, Select, InputNumber } from "antd";
import callApi from "../../api/ApiCaller";

export default function ModalForm() {
  const [visible, setVisible] = useState(false);
  const [isLoad, setIsLoad] = useState(false);

  const showModal = () => {
    setVisible(true);
  };

  const onFinish = async (value) => {
    await callApi("post", "products", value);
    console.log(value);
    setVisible(false);
    setIsLoad(false);
    message.success('Add new product success!')
  };

  return (
    <>
      <Button block type="primary" onClick={() => showModal()}>
        Add a Product
      </Button>
      <Modal
        closable={false}
        title="Add a product into the table"
        visible={visible}
        footer={null}
      >
        <Form
          initialValue={true}
          onFinish={onFinish}
          wrapperCol={{ span: 20 }}
          layout="horizontal"
        >
          <Form.Item name="product">
            <Input placeholder="Product" />
          </Form.Item>
          <Form.Item name="cost">
            <InputNumber placeholder="Cost" />
          </Form.Item>
          <Form.Item name="description">
            <Input placeholder="Description" />
          </Form.Item>
          <Form.Item name="stock">
            <Select placeholder="Stock">
              <Select.Option value="In Stock">In Stock</Select.Option>
              <Select.Option value="Out Stock">Out Stock</Select.Option>
            </Select>
          </Form.Item>
          <Form.Item>
            <Button
              onClick={() => setIsLoad(true)}
              loading={isLoad}
              style={{ margin: 5 }}
              type="primary"
              htmlType="submit"
            >
              Add
            </Button>
            <Button onClick={() => setVisible(false)}>Cancel</Button>
          </Form.Item>
        </Form>
      </Modal>
    </>
  );
}
