import React from "react";
import { Badge, Avatar, Popover, List } from "antd";
import { BellOutlined, SettingOutlined } from "@ant-design/icons";

export default function Notification() {
  const data = [
    { title: "Racing car sprays burning fuel into crowd" },
    { title: "Japanese princess to wed commoner" },
    { title: "Australian walks 100km after outback crash" },
    { title: "Man charged over missing wedding girl" },
    { title: "Los Angeles battles huge wildfiresRacing car" },
  ];
  const text = <span>Notification</span>;
  const content = (
    <div style={{ width: 250 }}>
      <List
        size="small"
        dataSource={data}
        renderItem={(item) => (
          <List.Item>
            <List.Item.Meta
              avatar={
                <Avatar>
                  <div className="icons-list"></div>
                  <SettingOutlined />
                </Avatar>
              }
              title={<a href="https://ant.design">{item.title}</a>}
              description="Ant Design, a design language for background applications"
            />
          </List.Item>
        )}
      />
    </div>
  );
  return (
    <div>
      <Popover
        placement="bottom"
        title={text}
        content={content}
        trigger="click"
      >
        <Badge count={data.length}>
          <Avatar
            style={{ backgroundColor: "grey", cursor: "pointer" }}
            icon={<BellOutlined />}
          />
        </Badge>
      </Popover>
    </div>
  );
}
