import React, { useEffect, useState } from "react";
import { Badge, Avatar, Popover, List } from "antd";
import { BellOutlined, SettingOutlined } from "@ant-design/icons";
import { useHistory } from "react-router-dom";
import { useDispatch } from "react-redux";
import { selectMenuX } from "../../action/SelectMenu";
import {fetchNotifications} from '../../action/DataNotifications';
// import dataNotification from "../profileUser/DataNotification";
import callApi from "../../api/CallApi";

export default function Notification() {

  const [data, setData] = useState();
  useEffect(() => {
    dispatch(fetchNotifications());
  }, [dispatch]);
  console.log(data);

  const text = (
    <div style={{ display: "flex" }}>
      <p style={{ margin: "0px auto" }} href>
        Notifications
      </p>
    </div>
  );
  const history = useHistory();
  const dispatch = useDispatch();
  const showAllNotification = () => {
    history.push("/profile/notification");
    dispatch(selectMenuX(5));
  };
  const content = (
    <div style={{ width: 250 }}>
      <List
        footer={
          <div style={{ display: "flex" }}>
            <a
              onClick={showAllNotification}
              style={{ margin: "0px auto" }}
              href
            >
              Show all notifications
            </a>
          </div>
        }
        size="small"
        dataSource={data}
        renderItem={(item) => (
          <List.Item>
            <a href>
              <div style={{ display: "flex", alignItems: "center" }}>
                <div style={{ marginRight: 7 }}>
                  <Avatar style={{ backgroundColor: "#3B5998" }}>
                    <SettingOutlined />
                  </Avatar>
                </div>
                <a href>{item.title}</a>
              </div>
            </a>
          </List.Item>
        )}
      />
    </div>
  );
  const hey = () =>{
    if (data) {
      return(
        <div>
        <Popover
          placement="bottom"
          title={text}
          content={content}
          trigger="click"
        >
          <Badge count={data.length}>
            <Avatar
              style={{ backgroundColor: "#4267b2", cursor: "pointer" }}
              icon={<BellOutlined />}
            />
          </Badge>
        </Popover>
      </div>
      )
    }
        else{
          return null;
        }
  }
  return (
    hey()
  );
}
