import React, { useState } from "react";
import { List, InputNumber, Space } from "antd";
import { MessageOutlined, LikeOutlined, StarOutlined } from "@ant-design/icons";

const listData = [
  { 
    id: 1,
    href: "#",
    title: `Iphone 11`,
    description:
      "Iphone 11 is special in 2020 by the Apple",
    price: 1000,
  },
  {
    id: 2,
    href: "#",
    title: `Samsung note 10`,
    description:
      "Samsung note 10 is special in 2020 by the Samsung",
    price: 900,
  },
  {
    id: 3,
    href: "#",
    title: `Samsung note 10`,
    description:
      "Samsung note 10 is special in 2020 by the Samsung",
    price: 900,
  },
];

//T
var totalBill = 0;
 listData.forEach(item => {
   totalBill = totalBill + item.price
 });


const IconText = ({ icon, text }) => (
  <Space>
    {React.createElement(icon)}
    {text}
  </Space>
);
export default function Cart() {
  const [amount, setAmount] = useState(1);

  return (
    <List
      itemLayout="vertical"
      size="large"
      pagination={{ pageSize: 3 }}
      dataSource={listData}
      footer={
        <h1>
          <b>Total Bill</b> {totalBill}
        </h1>
      }
      renderItem={(item) => (
        <List.Item
          key={item.title}
          actions={[
            <IconText
              icon={StarOutlined}
              text="156"
              key="list-vertical-star-o"
            />,
            <IconText
              icon={LikeOutlined}
              text="156"
              key="list-vertical-like-o"
            />,
            <IconText
              icon={MessageOutlined}
              text="2"
              key="list-vertical-message"
            />,
          ]}
          extra={
            <a href={item.href}>
              <img
                width={150}
                alt="logo"
                src="https://gw.alipayobjects.com/zos/rmsportal/mqaQswcyDLcXyDKnZfES.png"
              />
            </a>
          }
        >
          <List.Item.Meta
            title={<a href={item.href}>{item.title}</a>}
            description={item.description}
          />
          {item.price}
          <div style={{ display: "flex" }}>
            <InputNumber min={0} placeholder="Số lượng"/>
          </div>
        </List.Item>
      )}
    />
  );
}
