import React from "react";
import "./App.css";
import { Layout, BackTop } from "antd";
import "antd/dist/antd.css";
import { BrowserRouter as Router, Switch, Route } from "react-router-dom";
import Cart from "./components/cart/Cart";
import Type1 from "./components/Volcano";
import Type2 from "./components/Type2";
import Type3 from "./components/Type3";
import NotFound from "./components/notFound";
import ProfileUser from "./components/profileUser/ProfileUser";
import MenuHeader from "./components/MenuHeader";
import ProductsList from "./components/home/ProductsList";
import FooterPage from "./components/FooterPage";
import ButtonCart from "./components/home/ButtonCart";
import Search from "./components/home/Search";
import Notification from "./components/home/Notification";
import LoginNavbar from "./components/home/LoginNavbar";
import CarouselHome from "./components/home/carousel/Carousel";

function App() {
  const { Content } = Layout;
  return (
    <Router>
      <BackTop />
      <Layout className="layout">
        <MenuHeader />
        <div style={{ display: "flex", padding: 15, backgroundColor: "black" }}>
          <ButtonCart />
          <Search />
          <Notification />
          <LoginNavbar />
        </div>
        <CarouselHome />
        <Content>
          <Switch>
            <Route path="/" exact component={ProductsList} />
            <Route path="/type1" component={Type1} />
            <Route path="/type2" component={Type2} />
            <Route path="/type3" component={Type3} />
            <Route path="/cart" component={Cart} />
            <Route path="/profile" component={ProfileUser} />
            <Route path="/*" component={NotFound} />
          </Switch>
        </Content>
        <FooterPage />
      </Layout>
    </Router>
  );
}

export default App;
