import React, { useEffect, useState } from "react";
import { Carousel } from "antd";
import "./Carousel.css";
import callApi from "../../../api/ApiCallerCart";

export default function CarouselHome() {

  const [data, setData] = useState();
  useEffect(() => {
    async function fetchData() {
      await callApi("get", "Carousel", null)
        .then((res) => {
          setData(res.data);
        })
        .catch((err) => console.log(err));
    }
    fetchData();
  }, []);

  console.log(data);
  const loadCarousel = () => {
    data.map((item,) => (
      <div>
        <img alt="Carousel" src={item.image} />
      </div>
    ));
  };
  return (
    <Carousel effect="fade" autoplay>
      {loadCarousel()}
      {/* <div>
