import React from "react";
import { Badge, Avatar, Popover, List } from "antd";
import { BellOutlined } from "@ant-design/icons";

export default function Notification() {
  const data = [
    <a href>Racing car sprays burning fuel into crowd</a>,
    <a href>Japanese princess to wed commoner</a>,
    <a href>Australian walks 100km after outback crash</a>,
    <a href>Man charged over missing wedding girl</a>,
    <a href>Los Angeles battles huge wildfiresRacing car</a>,
  ];
  const text = <span style={{ justifyContent: "center" }}>Notification</span>;
  const content = (
    <div style={{ width: 250 }}>
      <List
        size="small"
        dataSource={data}
        renderItem={(item) => <List.Item>{item}</List.Item>}
      />
    </div>
  );
  return (
    <div>
      <Popover
        placement="bottom"
        title={text}
        content={content}
        trigger="click"
      >
        <Badge count={data.length}>
          <Avatar
            style={{ backgroundColor: "grey", cursor: "pointer" }}
            icon={<BellOutlined />}
          />
        </Badge>
      </Popover>
    </div>
  );
}
