const initialState = { list: [], amount: 1 };

const addList = (state = initialState.list, action) => {
  switch (action.type) {
    case "ADD_LIST": {
      return [...state, action.payload];
    }
    default:
      return state;
  }
};
const incrementAmount = (state = initialState.amount, action) => {
  switch (action.type) {
    case "INCREMENT_AMOUNT": {
      return state
    }
    default:
      return state;
  }
};

export default addList;
