import React from "react";
import "./App.css";
import { Layout, Menu, Button } from "antd";
import "antd/dist/antd.css";
import { ShoppingCartOutlined } from "@ant-design/icons";

import { useSelector, useDispatch } from "react-redux";

import { BrowserRouter as Router, Switch, Route, Link } from "react-router-dom";
import routes from "./routes";

function App() {
  const { Content, Footer } = Layout;

  const dispatch = useDispatch();

  const routerComponent = routes.map((route, index) => (
    <Route
      key={index}
      path={route.path}
      exact={route.exact}
      children={<route.main />}
    />
  ));


  const menuHeader = routes.map((route, index) => (
    <Menu.Item key={index}>
      <Link to={route.path}>{route.title}</Link>
    </Menu.Item>
  ));

  return (
    <Router>
      <Layout className="layout">
        <Menu theme="light" mode="horizontal" defaultSelectedKeys={["0"]}>
          {menuHeader}
          <Button type="link" style={{ float: "right", marginTop: 9 }}>
            Login
          </Button>
          <Button type="link" style={{ float: "right", marginTop: 9 }}>
            Sign in
          </Button>
          <Button
            size="larger"
            type="link"
            style={{ float: "right", marginTop: 9 }}
          >
            <ShoppingCartOutlined />
            Cart
          </Button>
        </Menu>
        <Content style={{ padding: "30px 30px" }}>
          <Switch>{routerComponent}</Switch>
        </Content>
        <Footer style={{ textAlign: "center" }}>MY_CV@ using reactjs</Footer>
      </Layout>
    </Router>
  );
}

export default App;
