  //     <Link to={route.path}>{route.title}</Link>
  //   </Menu.Item>
  // ));
  const onClickMenu = (e) => {
    dispatch(selectMenuX(e.key));
  };
  return (
    <Router>
      <Layout className="layout">
        <Menu
          onClick={(e) => onClickMenu(e)}
          subMenuCloseDelay={0}
          theme="light"
          mode="horizontal"
          selectedKeys={[selectMenu]}
          defaultSelectedKeys={["0"]}
        >
          {/* {menuHeader} */}
          <Menu.Item key={0}>
      <Link to="/">Product</Link>
    </Menu.Item>
          <Menu.Item key={0}>
      <Link to="/contact">Contact</Link>
    </Menu.Item>
          <Menu.Item key={0}>
      <Link to="/">Product</Link>
    </Menu.Item>
        </Menu>
        <Content>
          <Switch>
            {/* {routerComponent} */}
            <Route
      key={index}
      path={route.path}
      exact={route.exact}
      children={<route.main />}
    />
            </Switch>
        </Content>
        <Footer style={{ textAlign: "center" }}>
          My project using Reactjs, Redux, Ant Design, React-router
        </Footer>
      </Layout>
    </Router>
  );
}

export default App;
