import React, { useState } from "react";
import { Button, Avatar, Popconfirm, Badge } from "antd";
import { QuestionCircleOutlined, UserOutlined } from "@ant-design/icons";
import ModalFormLogin from "../../components/ModalFormLogin";
import ModalFormSignin from "../../components/ModalFormSignin";
import { useSelector, useDispatch } from "react-redux";
import isLogin from './'

export default function LoginNavbar() {
  const isLogin = useSelector((state) => state.load)
  const dispatch = useDispatch();
  // const [isLogin, setIsLogin] = useState(true);

  const login = () => (
    <>
      <ModalFormLogin />
      <ModalFormSignin />
    </>
  );
  const logout = () => (
    <div style={{ float: "right" }}>
      <Badge count={1}>
        <Avatar
          style={{ backgroundColor: "#87d068" }}
          icon={<UserOutlined />}
        />
      </Badge>
      <Popconfirm
        placement="bottomRight"
        onConfirm={() => dispatch(isLoad())}
        title="Are you sureï¼Ÿ"
        icon={<QuestionCircleOutlined style={{ color: "red" }} />}
      >
        <Button type="link">Logout</Button>
      </Popconfirm>
    </div>
  );
  return <>{isLogin ? logout() : login()}</>;
}
