import callApi from "../api/ApiCaller";

const initialState = {
  loading: false,
  error: null,
  user: null,
};

async function fetchData() {
  const api = await callApi("get", "products", null);
}

const dataProducts = (state = [], action) => {
  switch (action.type) {
    case "FETCH_USER":
      return {
        loading: true,
        user: null,
        error: null,
      };
    case "FETCH_USER_SUCCESS": {
      return {
        loading: false,
        user: action.payload.user,
        error: null,
      };
    }
    case "FETCH_USER_FAILED": {
      return {
        loading: false,
        user: null,
        error: action.payload.error,
      };
    }
    default:
      return state;
  }
};
export default dataProducts;
