import React from "react";
import { List, Space, Button, message } from "antd";
import { CloseOutlined } from "@ant-design/icons";
import { useSelector, useDispatch } from "react-redux";
import { incrementAmount, decrementAmount } from "../../action/List";

export default function Cart() {
  const IconText = ({ icon, text }) => (
    <Space>
      {React.createElement(icon)}
      {text}
    </Space>
  );
  const listData = useSelector((state) => state.list);
  const dispatch = useDispatch();

  //Total price of products
  let totalBill = 0;
  listData.forEach((item) => {
    totalBill = totalBill + item.price * item.amount;
  });

  const increment = (id) => {
    listData.forEach((item) => {
      if (item.id === id) {
        dispatch(incrementAmount(id));
      }
    });
  };
  const decrement = (id) => {
    listData.forEach((item) => {
      if (item.id === id) {
        if (item.amount === 1) {
          message.info(`Amount of ${item.name} must be larger than 1`);
        }
        dispatch(decrementAmount(id));
      }
    });
  };
  return (
    <List
      itemLayout="vertical"
      size="large"
      pagination={{ pageSize: 10 }}
      dataSource={listData}
      footer={
        <div>
          <hr />
          <h1>Total Bill: {totalBill} $</h1>
        </div>
      }
      renderItem={(item) => (
        <List.Item
          key={item.title}
          actions={[<IconText onClick={()=> deleteLi} icon={CloseOutlined} text="delete" />]}
          extra={
            <a href="https://github.com/">
              <img width={150} alt="logo" src={item.image} />
            </a>
          }
        >
          <List.Item.Meta title={item.name} description={item.description} />
          <h3>Price {item.price} $</h3>
          <div style={{ display: "flex" }}>
            <h3>Amount</h3>
            <Button
              size="small"
              style={{ margin: "0 5px" }}
              onClick={() => decrement(item.id)}
            >
              -
            </Button>
            <h3>{item.amount}</h3>
            <Button
              size="small"
              style={{ margin: "0 5px" }}
              onClick={() => increment(item.id)}
            >
              +
            </Button>
          </div>
        </List.Item>
      )}
    />
  );
}
