import callApi from "../api/ApiCaller";

const initialState = {
  loading: false,
  error: null,
  user: null,
};

async function fetchData() {
  const api = await callApi("get", "products", null);
}

const dataProducts = (state = initialState, action) => {
  switch (action.type) {
