const initialState = {
  list: [],
  amount: 1
}
const List = (state = initialState.list, action) => {
  switch (action.type) {
    case "ADD_LIST": {
      return [...state, action.payload];
    }
    case "INCREMENT_AMOUNT": {
      return [...state, action.payload];
    }
    default:
      return state;
  }
}

export default List;

