export const addList = (item) => {
  return {
    type: "ADD_LIST",
    payload: item,
  };
};
export const incrementAmount = (item) => {
  return {
    type: "INCREMENT_AMOUNT",
    payload: item,
  };
};
export const decrementAmount = (item) => {
  return {
    type: "DECREMENT_AMOUNT",
    payload: item,
  };
};
