export default function cart(state = [], action) {
  switch (action.type) {
    case "ADD_LIST": {
      return [...state, action.payload];
    }
    case "INCREMENT_AMOUNT": {
      state.forEach(element => {
        if (element.id === action.payload) {
          return element.amount+1
        }
      })
      return ;
    }
    case "DECREMENT_AMOUNT": {
      return [...state, action.payload];
    }
    default:
      return state;
  }
}

