import React, { useState } from "react";
import "./App.css";
import { Layout, Menu, Button, Avatar, Popconfirm,Badge } from "antd";
import "antd/dist/antd.css";
import { BrowserRouter as Router, Switch, Route, Link } from "react-router-dom";
import *as routes from "./routes";
import { QuestionCircleOutlined,UserOutlined } from "@ant-design/icons";
import ModalFormLogin from "./components/ModalFormLogin";
import ModalFormLogout from "./components/ModalFormSignin";

function App() {
  const [isLogin, setIsLogin] = useState(true);
  const { Content, Footer } = Layout;

  const routerComponent = routes.map((route, index) => (
    <Route
      key={index}
      path={route.path}
      exact={route.exact}
      children={<route.main />}
    />
  ));

  const menuHeader = routes.map((route, index) => (
    <Menu.Item key={index}>
      <Link to={route.path}>{route.title}</Link>
    </Menu.Item>
  ));

  const login = () => (
    <>
      <ModalFormLogin />
      <ModalFormLogout />
    </>
  );
  const logout = () => (
    <div style={{ float: "right" }}>
      <Badge count={1}>
      <Avatar style={{ backgroundColor: '#87d068' }} icon={<UserOutlined />} />
      </Badge>
      <Popconfirm
        placement="bottomRight"
        onConfirm={() => setIsLogin(false)}
        title="Are you sureï¼Ÿ"
        icon={<QuestionCircleOutlined style={{ color: "red" }} />}
      >
        <Button type="link">
          Logout
        </Button>
      </Popconfirm>
    </div>
  );

  return (
    <Router>
      <Layout className="layout">
        <Menu theme="light" mode="horizontal" defaultSelectedKeys={["0"]}>
          {menuHeader}
          {isLogin ? logout() : login()}
        </Menu>
        <Content>
          <Switch>{routerComponent}</Switch>
        </Content>
        <Footer style={{ textAlign: "center" }}>MY_CV@ using reactjs</Footer>
      </Layout>
    </Router>
  );
}

export default App;
