import React, { useEffect, useState } from "react";
import { useDispatch, useSelector } from "react-redux";
import { addList } from "../../action/List";
import "antd/dist/antd.css";
import { List, Card, message, Button } from "antd";
import { ShoppingCartOutlined } from "@ant-design/icons";
import CarouselHome from "./carousel/Carousel";
import callApi from "../../api/ApiCaller";
import ButtonGroup from "antd/lib/button/button-group";

export default function Home() {
  const [data, setData] = useState();
  const [load, setLoad] = useState(true)

  const dispatch = useDispatch();
  const listCart = useSelector((state) => state.list);

  useEffect(() => {
    async function fetchData() {
      await callApi("get", "products", null)
        .then((res) => {
          setData(res.data);
        })
        .catch((err) => console.log(err));
        setLoad(false)
    }
    fetchData();
  }, []);

  //Send a product to CART
  const sendProduct = (id) => {
    listCart.forEach((e) => {
      if (e.id === id) {
        id = null;
        message.info(`${e.name} is already in cart`);
      }
    });
    data.forEach((item) => {
      if (item.id === id) {
        dispatch(addList(item));
        message.success(`${item.name} add to Cart successfully!`);
      }
    });
  };

  return (
    <>
      <CarouselHome />
      <List
        grid={{
          gutter: 16,
          xs: 1,
          sm: 2,
          md: 3,
          lg: 4,
          xl: 5,
          xxl: 6,
        }}
        loading={load}
        pagination="true"
        dataSource={data}
        renderItem={(item) => (
          <List.Item>
            <Card
              cover={<img alt="Products list" src={item.image} />}
              style={{ textAlign: "center" }}
              hoverable
              title={item.name}
            >
              {item.description}
              <br/>
              <ButtonGroup>
                <Button style={{margin: 5}} danger onClick={() => sendProduct(item.id)}>
                Buy Now!
              </Button>
              <Button style={{margin: 5}} danger onClick={() => sendProduct(item.id)}>
                A <ShoppingCartOutlined />
              </Button>
              </ButtonGroup>

            </Card>
          </List.Item>
        )}
      />
    </>
  );
}
