import React from "react";
import "antd/dist/antd.css";
import {
  Table,
  Tag,
  Button,
  Form,
  Input,
  Select,
  DatePicker,
  InputNumber,
} from "antd";

const columns = [
  {
    title: "Name",
    dataIndex: "name",
    key: "name",
    render: (text) => text,
  },
  {
    title: "Age",
    dataIndex: "age",
    key: "age",
  },
  {
    title: "Address",
    dataIndex: "address",
    key: "address",
  },
  {
    title: "Permission",
    key: "Permission",
    dataIndex: "Permission",
    render: (Permission) => (
      <span>
        {Permission.map((tag) => {
          let color = tag.length > 5 ? "green" : "volcano";
          return (
            <Tag color={color} key={tag}>
              {tag.toUpperCase()}
            </Tag>
          );
        })}
      </span>
    ),
  },
  {
    title: "Action",
    key: "action",
    render: (text, record) => (
      <span>
        <Button primary>Edit</Button>
        <Button danger>Delete</Button>
      </span>
    ),
  },
];

const data = [
  {
    key: "1",
    name: "John Brown",
    age: 32,
    address: "New York No. 1 Lake Park",
    Permission: ["Admin"],
  },
  {
    key: "2",
    name: "Jim Green",
    age: 42,
    address: "London No. 1 Lake Park",
    Permission: ["Manager"],
  },
  {
    key: "3",
    name: "Joe Black",
    age: 32,
    address: "Sidney No. 1 Lake Park",
    Permission: ["Normal"],
  },
];

export default function ManagerUsers() {
  return (
    <div>
      <Table rowKey="uid" columns={columns} dataSource={data} />
      <Form labelCol={{ span: 4 }} wrapperCol={{ span: 40 }} layout="inline">
        <Form.Item>
          <Input placeholder="Product" />
        </Form.Item>
        <Form.Item>
          <InputNumber placeholder="Cost" />
        </Form.Item>
        <Form.Item>
          <Input placeholder="Address" />
        </Form.Item>
        <Form.Item>
          <Select placeholder="Stock">
            <Select.Option value="Admin">Admin</Select.Option>
            <Select.Option value="Manager">Manager</Select.Option>
            <Select.Option value="Normal">Normal</Select.Option>
          </Select>
        </Form.Item>

        <Form.Item>
          <DatePicker placeholder="JoinDay" />
        </Form.Item>

        <Form.Item>
          <Button type="primary">Add</Button>
        </Form.Item>
      </Form>
    </div>
  );
}
