import React, { useEffect, useState } from "react";
import { useDispatch, useSelector } from "react-redux";
import { addList } from "../action/List";
import "antd/dist/antd.css";
import { List, Card, message, Button, Badge, Input, BackTop, Space, Affix } from "antd";
import { ShoppingCartOutlined } from "@ant-design/icons";
import CarouselHome from "./carousel/Carousel";
import callApi from "../api/ApiCaller";
import { useHistory } from "react-router-dom";
import LoginNavbar from "./LoginNavbar";

export default function Home() {
  const { Search } = Input;
  const [data, setData] = useState();
  const [load, setLoad] = useState(true);
  const [dataSearch, setDataSearch] = useState();

  const dispatch = useDispatch();
  const listCart = useSelector((state) => state.list);

  const history = useHistory();
  const goToCart = () => {
    history.push("/cart");
  };

  useEffect(() => {
    async function fetchData() {
      await callApi("get", "products", null)
        .then((res) => {
          setData(res.data);
          setDataSearch(res.data);
        })
        .catch((err) => console.log(err));
      setLoad(false);
    }
    fetchData();
  }, []);

  const sendProduct = (id) => {
    listCart.forEach((e) => {
      if (e.id === id) {
        id = null;
        message.info(`${e.name} is already in cart`);
      }
    });
    data.forEach((item) => {
      if (item.id === id) {
        dispatch(addList(item));
      }
    });
  };
  const onSearch = (value) => {
    const listSearch = [];
    dataSearch.forEach((item) => {
      if (item.name.indexOf(value) !== -1) {
        listSearch.push(item);
      }
    });
    setData(listSearch);
  };

  return (
    <>
      <BackTop />
      <Space
        style={{
          display: "inline-flex",
          margin: 7,
        }}
      >
        <Affix offsetTop={10}>
        <Button
          onClick={() => goToCart()}
          style={{
            background: '#004400',
            padding: 10,
            borderRadius: "7px",
          }}
          type="primary"
          size="large"

        >
          <Badge count={listCart.length} showZero>
            <span style={{ fontSize: "1.5em" }}>Cart</span>
            <ShoppingCartOutlined style={{ fontSize: "1.5em",margin:4 }} />
          </Badge>
        </Button>
        </Affix>
        <Search
          allowClear="true"
          onSearch={(value) => onSearch(value)}
          enterButton
          placeholder="Search..."
        />
        <LoginNavbar />
      </Space>

      <CarouselHome />

      <List
        grid={{
          gutter: 10,
          xs: 1,
          sm: 2,
          md: 2,
          lg: 3,
          xl: 3,
          xxl: 4,
        }}
        loading={load}
        pagination={{  hideOnSinglePage: true }}
        dataSource={data}
        renderItem={(item) => (
          <List.Item>
            <Card
              cover={<img alt="Products list" src={item.image} />}
              style={{ textAlign: "center" }}
              hoverable="true"
            >
              <h3 style={{ color: "blue" }}>{item.name}</h3>
              <div style={{ height: 60 }}>{item.description}</div>
              <h3>{item.price.toLocaleString()} $</h3>
              <Button
                style={{ margin: 5 }}
                danger
                onClick={() => sendProduct(item.id)}
              >
                Add to Cart <ShoppingCartOutlined />
              </Button>
            </Card>
          </List.Item>
        )}
      />
    </>
  );
}
