const initialState = [];
const addList = (state = initialState, action) => {
  switch (action.type) {
    case "ADD_LIST": {
      return [...state, action.payload];
    }
    case "INCREMENT_AMOUNT": {
      const payload = action.payload
      return [...state, state.payload ];
    }
    case "DECREMENT_AMOUNT": {
      return [...state, state.payload];
    }
    default:
      return state;
  }
};

export default addList;
