import React, { useState } from "react";
import "./App.css";
import { Layout, Menu, Button, Avatar, Popconfirm,Badge } from "antd";
import "antd/dist/antd.css";
import { BrowserRouter as Router, Switch, Route, Link } from "react-router-dom";
import  routes from "./routes";
import LoginNavbar from './components/loginNavbar/LoginNavbar';
import Home from "./components/home/ProductsList";
import Index from "./components/Table/Index";
import Cart from "./components/cart/Cart";


function App() {
  const { Content, Footer } = Layout;

  // const routerComponent = routes.map((route, index) => (
  //   <Route
  //     key={index}
  //     path={route.path}
  //     exact={route.exact}
  //     children={<route.main />}
  //   />
  // ));

  const menuHeader = routes.map((route, index) => (
    <Menu.Item key={index}>
      <Link to={route.path}>{route.title}</Link>
    </Menu.Item>
  ));



  return (
    <Router>
      <Layout className="layout">
        <Menu theme="light" mode="horizontal" defaultSelectedKeys={["0"]}>
          {/* {menuHeader} */}
          <Menu.Item>
            <Link to="/">Home</Link>
          </Menu.Item>
          <Menu.Item>
            <Link to="/contact">Contact</Link>
          </Menu.Item>
          <Menu.Item>
            <Link to="/cart">Cart</Link>
          </Menu.Item>
          <Menu.Item>
            <Link to="/"></Link>
          </Menu.Item>
          <LoginNavbar/>
        </Menu>
        <Content>
          <Switch>
            <Route exact path="/" component={Home}/>
            <Route path="/contact" component={<h3>Contact</h3>}/>
            <Route path="/table" component={Index}/>
            <Route path="/cart" component={Cart}/>
          </Switch>
        </Content>
        <Footer style={{ textAlign: "center" }}>MY_CV@ using reactjs</Footer>
      </Layout>
    </Router>
  );
}

export default App;
