import fetchProductsApi from "../api/ApiCaller";

export const fetchProducts = () => {
  return async (dispatch) => {
    const res = await fetchProductsApi('get','products',null);
    const data = res.data;
    dispatch(getProducts(data[0]));
  };
};

export const getProducts = (products) => ({
  type: "GET_PRODUCTS",
  payload: products,
});

