            <Link to="/table">Table</Link>
          </Menu.Item>
          <Menu.Item>
            <Link to="/cart">Cart</Link>
          </Menu.Item>
          <LoginNavbar/>
        </Menu> */}

        <Content>
          <Switch>
            <Route exact path="/" component={Home}/>
            <Route path="/contact" component={Contact}/>
            <Route path="/table" component={Index}/>
            <Route path="/cart" component={Cart}/>
          </Switch>
        </Content>
        <Footer style={{ textAlign: "center" }}>MY_CV@ using reactjs</Footer>
      </Layout>
    </Router>
  );
}

export default App;
