const addList = (state = [], action) => {
  switch (action.type) {
    case "ADD_LIST": {
      return [...state, action.payload];
    }
    case "INCREMENT_AMOUNT": {
      return action.payload
    }
    default:
      return state;
  }
};


export default addList;

