import React from "react";
import {
  BrowserRouter as Router,
  Route,
  Switch,
  useRouteMatch,
  useParams,
  useHistory,
  Link,
} from "react-router-dom";
import Profile from "./Profile";
import Notification from "./Notification";
import AddressManagement from "./AddressManagement";
import NotFound from "../NotFound";
import { Menu, Layout } from "antd";

export default function ProfileUser() {
  const match = useRouteMatch();
  const params = useParams();
  const history = useHistory();
  // console.log(history);
  // console.log(params);
  // console.log(match);
  // const handleClick = () => {
  //   history.push("/");
  // };
  const { Content } = Layout;
  return (
    <Router>
      <div style={{ display: "flex" }}>
        <Menu
          // onClick={this.handleClick}
          style={{ width: 200 }}
          defaultSelectedKeys={["0"]}
          // defaultOpenKeys={["0"]}
          mode="inline"
        >
          <Menu.Item key="0">
            <Link to={`${match.url}/profile`}>Your Profile</Link>
          </Menu.Item>
          <Menu.Item key="1">
            <Link to={`${match.url}/notification`}>Notification</Link>
          </Menu.Item>
          <Menu.Item key="2">
            <Link to={`${match.url}/addressManagement`}>
              address management
            </Link>
          </Menu.Item>
        </Menu>
        <Content style={{ minHeight: 700 }}>
          <Switch>
            <Route exact path={`${match.url}/profile`} component={Profile} />
            <Route
              path={`${match.url}/notification`}
              component={Notification}
            />
            <Route
              path={`${match.url}/addressManagement`}
              component={AddressManagement}
            />
            <Route path={`${match.url}/*`} component={NotFound} />
          </Switch>
        </Content>
      </div>
    </Router>
  );
}
