const initialState = []
const addList = (state = initialState, action) => {
  switch (action.type) {
    case "ADD_LIST": {
      const newList = [...state];
      newList.push(action.pa);
      return {
        state: newList,
      };
    }
    default:
      return state;
  }
};
export default addList;
