import React, { useState } from "react";
import "./App.css";
import { Layout, Menu, Button, Avatar, Popconfirm } from "antd";
import "antd/dist/antd.css";
import { BrowserRouter as Router, Switch, Route, Link } from "react-router-dom";
import routes from "./routes";
import { QuestionCircleOutlined } from "@ant-design/icons";

function App() {
  const [isLogin, setIsLogin] = useState(true);
  const { Content, Footer } = Layout;

  const routerComponent = routes.map((route, index) => (
    <Route
      key={index}
      path={route.path}
      exact={route.exact}
      children={<route.main />}
    />
  ));

  const menuHeader = routes.map((route, index) => (
    <Menu.Item key={index}>
      <Link to={route.path}>{route.title}</Link>
    </Menu.Item>
  ));

  const login = () => (
    <>
      <Button type="link" style={{ float: "right", marginTop: 9 }}>
        Sign in
      </Button>
      <Button type="link" style={{ float: "right", marginTop: 9 }}>
        Login
      </Button>
    </>
  );
  const logout = () => (
    <>
      <Popconfirm
        placement="BR"
        onConfirm={() => setIsLogin(false)}
        title="Are you sureï¼Ÿ"
        icon={<QuestionCircleOutlined style={{ color: "red" }} />}
      >
        <Button
          // onClick={() => setIsLogin(false)}
          type="link"
          style={{ float: "right", marginTop: 9 }}
        >
          Logout
        </Button>
      </Popconfirm>

      <Avatar style={{ float: "right", marginTop: 9 }} />
    </>
  );

  return (
    <Router>
      <Layout className="layout">
        <Menu theme="light" mode="horizontal" defaultSelectedKeys={["0"]}>
          {menuHeader}
          {isLogin ? logout() : login()}
        </Menu>
        <Content>
          <Switch>{routerComponent}</Switch>
        </Content>
        <Footer style={{ textAlign: "center" }}>MY_CV@ using reactjs</Footer>
      </Layout>
    </Router>
  );
}

export default App;
