const initialState = {
  list: [],
  status: true,
}
const list = (state = initialState, action) => {
  switch(action.type){
      case 'ADD_LIST': {
        const newList = [...state.list]
        newList.push(action.payload)
        return {
          ...state,
          list: newList
        }
      }


      default : 
      return state;
  }
}
export default list;
