export default function cart(state = [], action) {
  switch (action.type) {
    case "ADD_LIST": {
      return [...state, action.payload];
    }
    case "INCREMENT_AMOUNT": {
      const changed = state.forEach((e, i) => {
        if (e.id === action.payload) {
          e.amount ++;
        }
        return [...state,];
      });
      return changed;
    }
    case "DECREMENT_AMOUNT": {
      return [...state, action.payload];
    }
    default:
      return state;
  }
}

