import React from "react";
import "./App.css";
import { Layout, Menu, Button } from "antd";
import "antd/dist/antd.css";
import { ShoppingCartOutlined } from "@ant-design/icons";

import { useSelector, useDispatch } from "react-redux";
import { increment, decrement } from "./action/Counter";
import { addList } from "./action/List";

import { BrowserRouter as Router, Switch, Route, Link } from "react-router-dom";
import routes from "./routes";

function App() {
  const { Content, Footer } = Layout;

  const counter = useSelector((state) => state.count);
  const list = useSelector((state) => state.list);
  const dispatch = useDispatch();

  const handleAddList = () => {
    const newList = {
      id: 1,
      title: "title of list",
    };
    const action = addList(newList);
    dispatch(action);
  };

  const routerComponent = routes.map((route, index) => (
    <Route
      key={index}
      path={route.path}
      exact={route.exact}
      children={<route.main />}
    />
  ));

  console.log(list);

  const menuHeader = routes.map((route, index) => (
    <Menu.Item key={index}>
      <Link to={route.path}>{route.title}</Link>
    </Menu.Item>
  ));

  return (
    <Router>
      <Layout className="layout">
        <Menu theme="light" mode="horizontal" defaultSelectedKeys={["0"]}>
          {menuHeader}
          <Button type="link" style={{ float: "right", marginTop: 9 }}>
            Login
          </Button>
          <Button type="link" style={{ float: "right", marginTop: 9 }}>
            Sign in
          </Button>
          <Button
            size="larger"
            type="link"
            style={{ float: "right", marginTop: 9 }}
          >
            <ShoppingCartOutlined />
            Cart
          </Button>
        </Menu>
        <Content style={{ padding: "30px 30px" }}>
          <Switch>{routerComponent}</Switch>
        </Content>
        <button onClick={() => dispatch(increment())}>increment</button>
        My counter is {counter}
        <button onClick={() => dispatch(decrement())}> decrement </button>
        <button onClick={() => dispatch(handleAddList())}>addList</button>
        <Footer style={{ textAlign: "center" }}>MY_CV@ using reactjs</Footer>
      </Layout>
    </Router>
  );
}

export default App;
