  // });
  return (
    <Carousel effect="fade" autoplay>
      {console.log(data)}
      {data.map((item, key) => (
        <div key={key}>
          <img
            style={{ height: "100%", width: "100%", objectFit: "fill" }}
            src={item.image}
            alt="img"
          />
        </div>
      ))}

    </Carousel>
  );
}
