import React from "react";
import { Button, Avatar, Popconfirm, Badge } from "antd";
import { QuestionCircleOutlined, UserOutlined } from "@ant-design/icons";
import ModalFormLogin from "./ModalFormLogin";
import ModalFormSignin from "./ModalFormSignin";
import { useSelector, useDispatch } from "react-redux";
import { isLogin } from "../action/IsLogin";
import { useHistory } from "react-router-dom";

export default function LoginNavbar() {
  const login = useSelector((state) => state.login);
  const dispatch = useDispatch();
  const history = useHistory();


  const onClickAvatar =()=>{
    history.push("/profile");

  }

  const loginMenu = () => (
    <>
      <ModalFormLogin />
      <ModalFormSignin />
    </>
  );
  const logoutMenu = () => (
    <div style={{ marginTop: 11, display:"flex" }}>
      <Badge count={1}>
        <Avatar
          onClick={()=>onClickAvatar()}
          style={{ backgroundColor: "#87d068", cursor:"pointer"}}
          icon={<UserOutlined />}
        />
      </Badge>
      <Popconfirm
        placement="bottomRight"
        onConfirm={() => dispatch(isLogin())}
        title="Are you sure Logoutï¼Ÿ"
        icon={<QuestionCircleOutlined style={{ color: "red" }} />}
      >
        <Button type="link" style={{marginTop: 10,fontSize:'1.5em'}}>Logout</Button>
      </Popconfirm>
    </div>
  );
  return (
    <div style={{display:"flex"}}>
      {login ? logoutMenu() : loginMenu()}
    </div>
  );
}
