import React, { useState } from "react";
import { List, Button, message, Popconfirm } from "antd";
import { useSelector, useDispatch } from "react-redux";
import {
  incrementAmount,
  decrementAmount,
  removeList,
} from "../../action/List";

export default function Cart() {
  const listData = useSelector((state) => state.list);
  const dispatch = useDispatch();

  //Total price of products
  let totalBill = 0;
  listData.forEach((item) => {
    totalBill = totalBill + item.price * item.amount;
  });

  const increment = (id) => {
    listData.forEach((item) => {
      if (item.id === id) {
        dispatch(incrementAmount(id));
      }
    });
  };
  const decrement = (id) => {
    listData.forEach((item) => {
      if (item.id === id) {
        if (item.amount === 1) {
          message.info(`Amount of ${item.name} must be larger than 1`);
        }
        dispatch(decrementAmount(id));
      }
    });
  };

  const deleteList = (id) => {
    listData.forEach((item) => {
      if (item.id === id) {
        dispatch(removeList(id));
      }
    });
  };
  const [load, setLoad] = useState(false)
  const orderNow =()=>{
    setLoad(true)
    setTimeout(() => {
      setLoad(false)
      message.success()
    }, 1000);
  }
  return (
    <List
      itemLayout="vertical"
      size="large"
      pagination={{ pageSize: 10 }}
      dataSource={listData}
      footer={
        <div style={{textAlign: 'center'}}>
          <hr />
          <h1>Total Bill: <pan style={{color: 'red'}}>{totalBill.toLocaleString()}</pan> $</h1>
          <Button onClick={()=>orderNow()} loading={load} type="primary" danger>ORDER NOW!</Button>
        </div>
      }
      renderItem={(item) => (
        <List.Item
          key={item.title}
          extra={
            <a href="https://github.com/">
              <img width={150} alt="logo" src={item.image} />
            </a>
          }
        >
          <List.Item.Meta title={item.name} description={item.description} />
          <h3>Price {item.price} $</h3>
          <div style={{ display: "flex" }}>
            <h3>Amount</h3>
            <Button
              size="small"
              style={{ margin: "0 5px" }}
              onClick={() => decrement(item.id)}
            >
              -
            </Button>
            <h3>{item.amount}</h3>
            <Button
              size="small"
              style={{ margin: "0 5px" }}
              onClick={() => increment(item.id)}
            >
              +
            </Button>
          </div>
          <Popconfirm
            placement="bottomLeft"
            title={`Are you sure delete ${item.name} ?`}
            okText="Yes"
            cancelText="No"
            onConfirm={()=>deleteList(item.id)}
          >
            <Button type="link">
            Delete
          </Button>
          </Popconfirm>
          <hr/>
        </List.Item>
      )}
    />
  );
}
