import axios from "axios";
import API from "./API";

export default function CallAPI(method = "GET", endpoint, item) {
  return axios({
    method: method,
    url: `${API}/${endpoint}`,
    data: item,
  });
}
async function getUser() {
  try {
    const response = await axios.get('/user?ID=12345');
    console.log(response);
  } catch (error) {
    console.error(error);
  }
}
